package javabasics;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1=100;
int num2= 200;
int num3 = num1+num2;
System.out.println("Addition Value is:"+num3);
int num4 = num1-num2;
System.out.println("Subtraction Value is:"+num4);
int num5=num1*num2;
System.out.println("Multiplication Value is:" + num5);
int num6=num1/num2;
System.out.println("Division Value is:" + num6);
int num7=num1%num2;
System.out.println("Modulus Value is:" + num7);

	}

}
