package javabasics;

public class Ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int mark=100;
if(mark>90)
{System.out.println("O Grade");
	}
else if(mark>80)
{System.out.println("A+ Grade");}
else if(mark>70) {
	System.out.println("A Grade");
}
else if(mark>=45)
{System.out.println("B Grade");}
else {
	System.out.println("RA");
}
}

}
